mapper.select:
select * from ( select row_number() over(order by document_date asc) no, i.*, e2.name approver, c.code_name c_status, c2.code_name c_position
from member m join employees e on m.email = e.email join ing_table i on e.employee_id = i.employee_id join employees e2 on i.approver_id = e2.employee_id join common_code c on i.document_check = c.code_value join common_code c2 on e2.position = c2.code_value
where m.email = #{email, jdbcType=VARCHAR} and i.document_submit = 'Y' order by no desc ) where no = #{no}

select *
from (
select row_number() over(order by document_date asc) no, i.*
from member m join employees e 
on m.email = e.email join ing_table i
on e.employee_id = i.employee_id
where m.email = #{email, jdbcType=VARCHAR} and i.document_submit = 'N'
order by no desc
)
where no = #{no}

select * from ( select row_number() over(order by document_date asc) no, i.*, e2.name drafter, c.code_name c_status, c2.code_name c_position
from member m join employees e on m.email = e.email join ing_table i on e.employee_id = i.approver_id join employees e2 on i.employee_id = e2.employee_id join common_code c on i.document_check = c.code_value join common_code c2 on e.position = c2.code_value
where m.email = #{email, jdbcType=VARCHAR} and i.document_submit = 'Y' order by no desc ) where no = #{no}
