mapper.select:
select distinct c.* , e.name code_maker_name
from common_code c left join employees e
on code_maker = e.employee_id

	where ${code} = #{vo.code_title}
	where ${code} = #{vo.code_value}
	where ${code} = #{vo.code_used}
	where ${code} = #{vo.code_name}

order by ${code}

mapper.update:
update common_code
set code_title = #{code_title}, code_value = #{code_value}, code_used = #{code_used}, code_name = #{code_name}, code_birth = sysdate
where code_value = #{code_value}
